import chalk from 'chalk';
export function createDebugLogger(debugEnabled = false) {
    return function (str) {
        if (debugEnabled) {
            // tslint:disable-next-line: no-console
            console.log(`${chalk.bold('[debug]')} ${chalk.gray(`[${new Date().toISOString()}]`)} ${str}`);
        }
    };
}
